import React, { useState, useEffect, useRef } from "react";

function CounterGame() {
  const [count, setCount] = useState(0);
  const [powerUpX2, setPowerUpX2] = useState(false);
  const [powerUpTimer, setPowerUpTimer] = useState(0);
  const [bonusCombo, setBonusCombo] = useState(0);
  const comboRef = useRef(null);

  // ‚úÖ Power-up x2 ‡πÅ‡∏ö‡∏ö‡∏à‡∏≥‡∏Å‡∏±‡∏î‡πÄ‡∏ß‡∏•‡∏≤
  useEffect(() => {
    let timer;
    if (powerUpTimer > 0) {
      timer = setInterval(() => {
        setPowerUpTimer(prev => prev - 1);
      }, 1000);
    } else if (powerUpTimer === 0 && powerUpX2) {
      setPowerUpX2(false);
    }
    return () => clearInterval(timer);
  }, [powerUpTimer, powerUpX2]);

  // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡πà‡∏≤ ‡∏û‡∏£‡πâ‡∏≠‡∏° Combo
  const handleIncrease = () => {
    let increment = 1;
    if (powerUpX2) increment *= 2;

    // Bonus Combo: ‡∏ñ‡πâ‡∏≤‡∏Å‡∏î‡∏ï‡πà‡∏≠‡πÄ‡∏ô‡∏∑‡πà‡∏≠‡∏á <2 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
    increment += bonusCombo;

    setCount(prev => prev + increment);

    // ‡πÄ‡∏û‡∏¥‡πà‡∏° combo
    setBonusCombo(prev => prev + 1);

    // ‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ï combo ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏Å‡∏î‡∏†‡∏≤‡∏¢‡πÉ‡∏ô 2 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
    clearTimeout(comboRef.current);
    comboRef.current = setTimeout(() => {
      setBonusCombo(0);
    }, 2000);
  };

  const handleDecrease = () => setCount(prev => prev - 1);

  // ‡∏Å‡∏î Power-up x2
  const activatePowerUp = () => {
    setPowerUpX2(true);
    setPowerUpTimer(5); // 5 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
  };

  const handleReset = () => {
    setCount(0);
    setPowerUpX2(false);
    setPowerUpTimer(0);
    setBonusCombo(0);
    clearTimeout(comboRef.current);
  };

  return (
    <div style={styles.container}>
      <h1>‚ö° Counter Game + Power-up ‚ö°</h1>
      <h2 style={{ fontSize: "2.5rem" }}>{count}</h2>

      <div style={styles.btnGroup}>
        <button style={styles.btn} onClick={handleDecrease}>‚ûñ</button>
        <button style={styles.btn} onClick={handleIncrease}>‚ûï</button>
      </div>

      <div style={{ marginTop: 20 }}>
        <button
          style={{
            ...styles.btn,
            backgroundColor: powerUpX2 ? "#ff9800" : "#555",
          }}
          onClick={activatePowerUp}
          disabled={powerUpX2}
        >
          üî• Power-up x2 {powerUpX2 ? `(${powerUpTimer}s)` : ""}
        </button>
      </div>

      <button style={{ ...styles.btn, marginTop: 25, background: "#e53935" }} onClick={handleReset}>
        ‚ôªÔ∏è Reset
      </button>

      <div style={{ marginTop: 25, color: "#ccc" }}>
        <p>Power-up x2: {powerUpX2 ? "Active" : "Inactive"}</p>
        <p>Bonus Combo: {bonusCombo}</p>
      </div>
    </div>
  );
}

// üé® ‡∏™‡πÑ‡∏ï‡∏•‡πå‡∏û‡∏∑‡πâ‡∏ô‡∏ê‡∏≤‡∏ô
const styles = {
  container: {
    textAlign: "center",
    padding: 40,
    background: "linear-gradient(135deg, #1e1e2f, #2b2b3d)",
    color: "white",
    borderRadius: 20,
    width: 350,
    margin: "40px auto",
    boxShadow: "0 0 15px rgba(255,255,255,0.2)",
  },
  btnGroup: {
    display: "flex",
    justifyContent: "center",
    gap: 15,
    marginTop: 15,
  },
  btn: {
    padding: "10px 18px",
    border: "none",
    borderRadius: 10,
    cursor: "pointer",
    backgroundColor: "#444",
    color: "white",
    fontSize: "1rem",
    transition: "0.2s",
  },
};

export default CounterGame;
