import React, { useState, useEffect } from "react";

function CounterGame() {
  const [count, setCount] = useState(0);
  const [powerUpX2, setPowerUpX2] = useState(false);
  const [autoIncrement, setAutoIncrement] = useState(false);

  // ‚úÖ ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£ Power-up ‡πÅ‡∏ö‡∏ö Auto +1 ‡∏ó‡∏∏‡∏Å‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
  useEffect(() => {
    let interval;
    if (autoIncrement) {
      interval = setInterval(() => {
        setCount(prev => prev + (powerUpX2 ? 2 : 1));
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [autoIncrement, powerUpX2]);

  // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡πà‡∏≤ (‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏à‡∏≤‡∏Å Power-up)
  const handleIncrease = () => {
    setCount(prev => prev + (powerUpX2 ? 2 : 1));
  };

  const handleDecrease = () => {
    setCount(prev => prev - 1);
  };

  const toggleX2 = () => {
    setPowerUpX2(prev => !prev);
  };

  const toggleAuto = () => {
    setAutoIncrement(prev => !prev);
  };

  const handleReset = () => {
    setCount(0);
    setPowerUpX2(false);
    setAutoIncrement(false);
  };

  return (
    <div style={styles.container}>
      <h1>‚ö° Counter Game ‚ö°</h1>
      <h2 style={{ fontSize: "2.5rem" }}>{count}</h2>

      <div style={styles.btnGroup}>
        <button style={styles.btn} onClick={handleDecrease}>‚ûñ</button>
        <button style={styles.btn} onClick={handleIncrease}>‚ûï</button>
      </div>

      <div style={{ marginTop: 20 }}>
        <button
          style={{
            ...styles.btn,
            backgroundColor: powerUpX2 ? "#ff9800" : "#555",
          }}
          onClick={toggleX2}
        >
          üî• Power-up x2 {powerUpX2 ? "ON" : "OFF"}
        </button>

        <button
          style={{
            ...styles.btn,
            backgroundColor: autoIncrement ? "#4caf50" : "#555",
          }}
          onClick={toggleAuto}
        >
          ‚è± Auto +1/sec {autoIncrement ? "ON" : "OFF"}
        </button>
      </div>

      <button style={{ ...styles.btn, marginTop: 25, background: "#e53935" }} onClick={handleReset}>
        ‚ôªÔ∏è Reset
      </button>

      <div style={{ marginTop: 25, color: "#ccc" }}>
        <p>Power-up x2: {powerUpX2 ? "Active" : "Inactive"}</p>
        <p>Auto Increment: {autoIncrement ? "Active" : "Inactive"}</p>
      </div>
    </div>
  );
}

// üé® ‡∏™‡πÑ‡∏ï‡∏•‡πå‡∏û‡∏∑‡πâ‡∏ô‡∏ê‡∏≤‡∏ô
const styles = {
  container: {
    textAlign: "center",
    padding: 40,
    background: "linear-gradient(135deg, #1e1e2f, #2b2b3d)",
    color: "white",
    borderRadius: 20,
    width: 350,
    margin: "40px auto",
    boxShadow: "0 0 15px rgba(255,255,255,0.2)",
  },
  btnGroup: {
    display: "flex",
    justifyContent: "center",
    gap: 15,
    marginTop: 15,
  },
  btn: {
    padding: "10px 18px",
    border: "none",
    borderRadius: 10,
    cursor: "pointer",
    backgroundColor: "#444",
    color: "white",
    fontSize: "1rem",
    transition: "0.2s",
  },
};

export default CounterGame;
