import React, { useState } from "react";

function Calculator() {
  const [input, setInput] = useState("");

  // ‚úÖ ‡∏Å‡∏î‡∏õ‡∏∏‡πà‡∏°‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç/‡πÄ‡∏Ñ‡∏£‡∏∑‡πà‡∏≠‡∏á‡∏´‡∏°‡∏≤‡∏¢
  const handleClick = (value) => {
    setInput((prev) => prev + value);
  };

  // ‚úÖ ‡πÄ‡∏Ñ‡∏•‡∏µ‡∏¢‡∏£‡πå
  const handleClear = () => setInput("");

  // ‚úÖ ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì
  const handleCalculate = () => {
    try {
      // ‡πÅ‡∏õ‡∏•‡∏á % ‡πÄ‡∏õ‡πá‡∏ô /100
      const formattedInput = input.replace(/%/g, "/100");
      setInput(eval(formattedInput).toString());
    } catch (err) {
      setInput("Error");
    }
  };

  // ‚úÖ ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏£‡∏≤‡∏Å‡∏ó‡∏µ‡πà‡∏™‡∏≠‡∏á ‚àö
  const handleSqrt = () => {
    try {
      const result = Math.sqrt(eval(input));
      setInput(result.toString());
    } catch (err) {
      setInput("Error");
    }
  };

  return (
    <div style={styles.container}>
      <h1>üßÆ Calculator</h1>
      <input type="text" value={input} readOnly style={styles.input} />

      <div style={styles.btnGrid}>
        {/* ‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç */}
        {[7, 8, 9, 4, 5, 6, 1, 2, 3, 0].map((num) => (
          <button key={num} style={styles.btn} onClick={() => handleClick(num.toString())}>
            {num}
          </button>
        ))}

        {/* ‡πÄ‡∏Ñ‡∏£‡∏∑‡πà‡∏≠‡∏á‡∏´‡∏°‡∏≤‡∏¢ */}
        {["+","-","*","/","."].map((op) => (
          <button key={op} style={styles.btn} onClick={() => handleClick(op)}>
            {op}
          </button>
        ))}

        {/* ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏û‡∏¥‡πÄ‡∏®‡∏© */}
        <button style={styles.btn} onClick={handleSqrt}>‚àö</button>
        <button style={styles.btn} onClick={() => handleClick("%")}>%</button>
        <button style={{ ...styles.btn, backgroundColor: "#e53935" }} onClick={handleClear}>C</button>
        <button style={{ ...styles.btn, backgroundColor: "#4caf50" }} onClick={handleCalculate}>=</button>
      </div>
    </div>
  );
}

// üé® ‡∏™‡πÑ‡∏ï‡∏•‡πå
const styles = {
  container: {
    width: 300,
    margin: "40px auto",
    padding: 20,
    textAlign: "center",
    background: "#f0f0f0",
    borderRadius: 15,
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  },
  input: {
    width: "100%",
    padding: 10,
    fontSize: 20,
    marginBottom: 10,
    textAlign: "right",
    borderRadius: 5,
    border: "1px solid #ccc",
  },
  btnGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: 10,
  },
  btn: {
    padding: "15px 0",
    fontSize: 18,
    borderRadius: 5,
    border: "none",
    cursor: "pointer",
    backgroundColor: "#2196f3",
    color: "white",
  },
};

export default Calculator;
