import React, { useState, useEffect } from 'react';

function CounterGame() {
  const [score, setScore] = useState(0);
  const [level, setLevel] = useState(1);
  const [timeLeft, setTimeLeft] = useState(30);
  const [isGameActive, setIsGameActive] = useState(false);
  const [highScore, setHighScore] = useState(0);
  const [multiplier, setMultiplier] = useState(1);
  const [clickStreak, setClickStreak] = useState(0);

  const [powerUps, setPowerUps] = useState({
    doublePoints: false,
    extraTime: false,
    autoClick: false,
  });

  const [powerUpTimers, setPowerUpTimers] = useState({
    doublePoints: 0,
    autoClick: 0,
  });

  // Timer Effect
  useEffect(() => {
    let timer;
    if (isGameActive && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            setIsGameActive(false);
            return 0;
          }
          return prev - 1;
        });

        // อัพเดท power-up timers
        setPowerUpTimers(prev => ({
          doublePoints: prev.doublePoints > 0 ? prev.doublePoints - 1 : 0,
          autoClick: prev.autoClick > 0 ? prev.autoClick - 1 : 0,
        }));
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isGameActive, timeLeft]);

  const startGame = () => {
    setScore(0);
    setLevel(1);
    setTimeLeft(30);
    setMultiplier(1);
    setClickStreak(0);
    setPowerUps({ doublePoints: false, extraTime: false, autoClick: false });
    setPowerUpTimers({ doublePoints: 0, autoClick: 0 });
    setIsGameActive(true);
  };

  const addScore = (points) => {
    if (!isGameActive) return;
    let finalPoints = points * multiplier;
    if (powerUps.doublePoints) finalPoints *= 2;
    const newScore = score + finalPoints;
    const newStreak = clickStreak + 1;

    setScore(newScore);
    setClickStreak(newStreak);

    const newLevel = Math.floor(newScore / 100) + 1;
    if (newLevel > level) {
      setLevel(newLevel);
      setMultiplier(1 + (newLevel - 1) * 0.5);
    }

    if (newStreak % 10 === 0) setScore(prev => prev + 50);
    if (newScore > highScore) setHighScore(newScore);
  };

  const activatePowerUp = (type) => {
    if (type === "doublePoints" && !powerUps.doublePoints) {
      setPowerUps(prev => ({ ...prev, doublePoints: true }));
      setPowerUpTimers(prev => ({ ...prev, doublePoints: 5 }));
      setTimeout(() => {
        setPowerUps(prev => ({ ...prev, doublePoints: false }));
      }, 5000);
    }
    if (type === "extraTime" && !powerUps.extraTime) {
      setTimeLeft(prev => prev + 10);
      setPowerUps(prev => ({ ...prev, extraTime: true }));
    }
    if (type === "autoClick" && !powerUps.autoClick) {
      setPowerUps(prev => ({ ...prev, autoClick: true }));
      setPowerUpTimers(prev => ({ ...prev, autoClick: 5 }));
      let auto = setInterval(() => addScore(1), 1000);
      setTimeout(() => {
        clearInterval(auto);
        setPowerUps(prev => ({ ...prev, autoClick: false }));
      }, 5000);
    }
  };

  const endGame = () => {
    setIsGameActive(false);
    if (score > highScore) setHighScore(score);
  };

  // Render Progress Bar
  const ProgressBar = ({ value, max, color }) => (
    <div className="w-full h-3 bg-gray-300 rounded-full overflow-hidden">
      <div
        className={`h-full ${color} transition-all duration-500`}
        style={{ width: `${(value / max) * 100}%` }}
      />
    </div>
  );

  return (
    <div className="max-w-lg mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-purple-600 text-center">
        🎮 เกมนับคะแนน (Power-ups Edition)
      </h2>

      {/* Stats */}
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="bg-blue-50 p-4 rounded-lg text-center shadow">
          <div className="text-2xl font-bold text-blue-600">{score}</div>
          <div className="text-sm text-gray-600">คะแนน</div>
        </div>
        <div className="bg-green-50 p-4 rounded-lg text-center shadow">
          <div className="text-2xl font-bold text-green-600">{highScore}</div>
          <div className="text-sm text-gray-600">คะแนนสูงสุด</div>
        </div>
        <div className="bg-purple-50 p-4 rounded-lg text-center shadow">
          <div className="text-2xl font-bold text-purple-600">Lv.{level}</div>
          <div className="text-sm text-gray-600">เลเวล</div>
        </div>
        <div className="bg-orange-50 p-4 rounded-lg text-center shadow">
          <div className="text-2xl font-bold text-orange-600">{timeLeft}s</div>
          <ProgressBar value={timeLeft} max={30 + (powerUps.extraTime ? 10 : 0)} color="bg-orange-400" />
          <div className="text-sm text-gray-600 mt-1">เวลาเหลือ</div>
        </div>
      </div>

      {/* Power-ups */}
      {isGameActive && (
        <div className="grid grid-cols-3 gap-2 mb-6">
          <button
            onClick={() => activatePowerUp("doublePoints")}
            disabled={powerUps.doublePoints}
            className={`px-3 py-2 rounded-lg text-white font-bold text-sm relative overflow-hidden ${powerUps.doublePoints ? "bg-gray-400" : "bg-yellow-500 hover:bg-yellow-600"}`}
          >
            ✨ Double Points
            {powerUps.doublePoints && (
              <div className="absolute inset-0 bg-yellow-200 opacity-30 animate-pulse rounded-lg" />
            )}
            {powerUpTimers.doublePoints > 0 && (
              <div className="absolute bottom-0 left-0 w-full h-1 bg-yellow-300 rounded-full">
                <div
                  className="h-full bg-yellow-500 transition-all duration-1000"
                  style={{ width: `${(powerUpTimers.doublePoints / 5) * 100}%` }}
                />
              </div>
            )}
          </button>

          <button
            onClick={() => activatePowerUp("extraTime")}
            disabled={powerUps.extraTime}
            className={`px-3 py-2 rounded-lg text-white font-bold text-sm ${powerUps.extraTime ? "bg-gray-400" : "bg-blue-500 hover:bg-blue-600"}`}
          >
            ⏱ +10s
          </button>

          <button
            onClick={() => activatePowerUp("autoClick")}
            disabled={powerUps.autoClick}
            className={`px-3 py-2 rounded-lg text-white font-bold text-sm relative overflow-hidden ${powerUps.autoClick ? "bg-gray-400" : "bg-green-500 hover:bg-green-600"}`}
          >
            🤖 Auto Click
            {powerUps.autoClick && (
              <div className="absolute inset-0 bg-green-200 opacity-30 animate-pulse rounded-lg" />
            )}
            {powerUpTimers.autoClick > 0 && (
              <div className="absolute bottom-0 left-0 w-full h-1 bg-green-300 rounded-full">
                <div
                  className="h-full bg-green-500 transition-all duration-1000"
                  style={{ width: `${(powerUpTimers.autoClick / 5) * 100}%` }}
                />
              </div>
            )}
          </button>
        </div>
      )}

      {/* Game Controls */}
      {!isGameActive ? (
        <div className="text-center mb-6">
          <button
            onClick={startGame}
            className="bg-purple-500 hover:bg-purple-600 text-white px-8 py-4 rounded-lg font-bold text-xl shadow-lg"
          >
            🚀 เริ่มเกม
          </button>
          {score > 0 && (
            <div className="mt-4 p-4 bg-gray-100 rounded-lg shadow">
              <p className="text-lg font-bold">เกมจบแล้ว!</p>
              <p>คะแนนรวม: <span className="text-purple-600 font-bold">{score}</span></p>
              <p>เลเวลสูงสุด: <span className="text-green-600 font-bold">{level}</span></p>
            </div>
          )}
        </div>
      ) : (
        <div className="space-y-4 mb-6">
          <div className="grid grid-cols-2 gap-4">
            <button
              onClick={() => addScore(1)}
              className="bg-green-500 hover:bg-green-600 text-white py-4 rounded-lg font-bold text-lg shadow-md transform hover:scale-105 transition-transform"
            >
              +1 คะแนน
            </button>
            <button
              onClick={() => addScore(5)}
              className="bg-blue-500 hover:bg-blue-600 text-white py-4 rounded-lg font-bold text-lg shadow-md transform hover:scale-105 transition-transform"
            >
              +5 คะแนน
            </button>
          </div>
          <button
            onClick={() => addScore(10)}
            className="w-full bg-red-500 hover:bg-red-600 text-white py-4 rounded-lg font-bold text-lg shadow-md transform hover:scale-105 transition-transform"
          >
            +10 คะแนน (โบนัส!)
          </button>
          <button
            onClick={endGame}
            className="w-full bg-gray-500 hover:bg-gray-600 text-white py-2 rounded-lg shadow"
          >
            หยุดเกม
          </button>
        </div>
      )}
    </div>
  );
}

export default CounterGame;
