import React, { useState } from "react";

function ColorPicker() {
  const [colors, setColors] = useState(["red", "green", "blue"]);
  const [selectedColor, setSelectedColor] = useState("");
  const [newColor, setNewColor] = useState("");

  // ‚úÖ ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏µ
  const handleSelectColor = (color) => {
    setSelectedColor(color);
  };

  // ‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏™‡∏µ‡πÉ‡∏´‡∏°‡πà
  const handleAddColor = () => {
    if (!newColor) return;
    // ‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ã‡πâ‡∏≥
    if (!colors.includes(newColor)) {
      setColors([...colors, newColor]);
      setNewColor("");
    } else {
      alert("‡∏™‡∏µ‡∏ô‡∏µ‡πâ‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß!");
    }
  };

  return (
    <div style={styles.container}>
      <h1>üé® Color Picker</h1>
      <div style={styles.colorList}>
        {colors.map((color, index) => (
          <div
            key={index}
            style={{
              ...styles.colorBox,
              backgroundColor: color,
              border: selectedColor === color ? "3px solid black" : "1px solid #ccc",
            }}
            onClick={() => handleSelectColor(color)}
          />
        ))}
      </div>

      <div style={{ marginTop: 20 }}>
        <input
          type="text"
          placeholder="‡πÉ‡∏™‡πà‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏µ‡∏´‡∏£‡∏∑‡∏≠ HEX"
          value={newColor}
          onChange={(e) => setNewColor(e.target.value)}
          style={styles.input}
        />
        <button style={styles.btn} onClick={handleAddColor}>
          ‚ûï Add Color
        </button>
      </div>

      {selectedColor && (
        <p style={{ marginTop: 20 }}>
          ‡∏™‡∏µ‡∏ó‡∏µ‡πà‡πÄ‡∏•‡∏∑‡∏≠‡∏Å: <span style={{ color: selectedColor }}>{selectedColor}</span>
        </p>
      )}
    </div>
  );
}

