const styles = {
  container: {
    textAlign: "center",
    padding: 30,
    width: 400,
    margin: "40px auto",
    background: "#f0f0f0",
    borderRadius: 15,
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  },
  colorList: {
