import React, { useState } from 'react';

function VotingSystem() {
  // State ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
  const [options, setOptions] = useState(['üçé ‡πÅ‡∏≠‡∏õ‡πÄ‡∏õ‡∏¥‡πâ‡∏•', 'üçå ‡∏Å‡∏•‡πâ‡∏ß‡∏¢', 'üçá ‡∏≠‡∏á‡∏∏‡πà‡∏ô', 'üçâ ‡πÅ‡∏ï‡∏á‡πÇ‡∏°']);
  const [votes, setVotes] = useState(Array(4).fill(0));

  // State ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÉ‡∏´‡∏°‡πà
  const [newOption, setNewOption] = useState("");

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÇ‡∏´‡∏ß‡∏ï
  const handleVote = (index) => {
    const newVotes = [...votes];
    newVotes[index] += 1;
    setVotes(newVotes);
  };

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÉ‡∏´‡∏°‡πà
  const handleAddOption = () => {
    if (!newOption.trim()) return;
    setOptions([...options, newOption.trim()]);
    setVotes([...votes, 0]);
    setNewOption("");
  };

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏•‡∏ö‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
  const handleDelete = (index) => {
    const newOptions = [...options];
    newOptions.splice(index, 1);
    setOptions(newOptions);

    const newVotes = [...votes];
    newVotes.splice(index, 1);
    setVotes(newVotes);
  };

  // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏£‡∏ß‡∏°
  const totalVotes = votes.reduce((a, b) => a + b, 0);

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">üó≥Ô∏è ‡∏£‡∏∞‡∏ö‡∏ö‡πÇ‡∏´‡∏ß‡∏ï‡∏ú‡∏•‡πÑ‡∏°‡πâ</h2>

      {/* ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÉ‡∏´‡∏°‡πà */}
      <div className="flex mb-6">
        <input
          type="text"
          placeholder="‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ú‡∏•‡πÑ‡∏°‡πâ‡πÉ‡∏´‡∏°‡πà"
          value={newOption}
          onChange={(e) => setNewOption(e.target.value)}
          className="flex-1 border rounded-l-lg p-2"
        />
        <button
          onClick={handleAddOption}
          className="bg-green-500 hover:bg-green-600 text-white font-semibold px-4 rounded-r-lg"
        >
          ‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°
        </button>
      </div>

      {/* ‡πÅ‡∏™‡∏î‡∏á‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÅ‡∏•‡∏∞‡πÇ‡∏´‡∏ß‡∏ï */}
      {options.map((option, index) => {
        const voteCount = votes[index];
        const percentage = totalVotes === 0 ? 0 : (voteCount / totalVotes) * 100;

        return (
