import React, { useState } from "react";

function BasicCounter() {
  const [count, setCount] = useState(0);

  // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏¥‡πà‡∏°/‡∏•‡∏î
  const increase = (value = 1) => setCount(prev => prev + value);
  const decrease = (value = 1) => setCount(prev => prev - value);

  const reset = () => setCount(0);

  return (
    <div style={styles.container}>
      <h1>üßÆ Basic Counter</h1>
      <h2 style={{ fontSize: "2.5rem" }}>{count}</h2>

      <div style={styles.btnGroup}>
        <button style={styles.btn} onClick={() => decrease()}>‚ûñ1</button>
        <button style={styles.btn} onClick={() => increase()}>‚ûï1</button>
        <button style={styles.btn} onClick={() => increase(5)}>‚ûï5</button>
        <button style={styles.btn} onClick={() => increase(10)}>‚ûï10</button>
      </div>

      <button style={{ ...styles.btn, marginTop: 20, backgroundColor: "#e53935" }} onClick={reset}>
        ‚ôªÔ∏è Reset
      </button>
    </div>
  );
}

// üé® ‡∏™‡πÑ‡∏ï‡∏•‡πå‡∏û‡∏∑‡πâ‡∏ô‡∏ê‡∏≤‡∏ô
const styles = {
  container: {
    textAlign: "center",
    padding: 30,
    width: 400,
    margin: "40px auto",
    background: "#f0f0f0",
    borderRadius: 15,
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  },
  btnGroup: {
    display: "flex",
    justifyContent: "center",
    gap: 10,
    flexWrap: "wrap",
    marginTop: 15,
  },
  btn: {
    padding: "10px 15px",
    border: "none",
    borderRadius: 8,
    cursor: "pointer",
    backgroundColor: "#2196f3",
    color: "white",
    fontSize: 16,
  },
};

export default BasicCounter;
