import React, { useState, useEffect } from 'react';

function Stopwatch() {
  const [time, setTime] = useState(0);
  const [running, setRunning] = useState(false);

  useEffect(() => {
    let timer;
    if (running) {
      timer = setInterval(() => {
        setTime((prev) => prev + 1);
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [running]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg max-w-sm mx-auto text-center">
      <h2 className="text-2xl font-bold mb-4">⏱️ Stopwatch</h2>
      <div className="text-3xl font-mono mb-4">{formatTime(time)}</div>

      <div className="flex justify-center gap-2">
        <button
          onClick={() => setRunning(true)}
          className="px-4 py-2 bg-green-500 hover:bg-green-600 text-white rounded"
        >
          Start
        </button>
        <button
          onClick={() => setRunning(false)}
          className="px-4 py-2 bg-yellow-500 hover:bg-yellow-600 text-white rounded"
        >
          Stop
        </button>
        <button
          onClick={() => { setTime(0); setRunning(false); }}
          className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded"
        >
          Reset
        </button>
      </div>
    </div>
  );
}

export default Stopwatch;
