import React, { useState, useEffect } from 'react';

function CounterGame() {
  // Game States
  const [score, setScore] = useState(0);
  const [level, setLevel] = useState(1);
  const [timeLeft, setTimeLeft] = useState(30);
  const [isGameActive, setIsGameActive] = useState(false);
  const [highScore, setHighScore] = useState(0);
  const [multiplier, setMultiplier] = useState(1);
  const [clickStreak, setClickStreak] = useState(0);

  // Power-ups
  const [powerUps, setPowerUps] = useState({
    doublePoints: false,
    extraTime: false,
    autoClick: false,
  });

  // Timer Effect
  useEffect(() => {
    let timer;
    if (isGameActive && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            setIsGameActive(false);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isGameActive, timeLeft]);

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏Å‡∏°
  const startGame = () => {
    setScore(0);
    setLevel(1);
    setTimeLeft(30);
    setMultiplier(1);
    setClickStreak(0);
    setPowerUps({ doublePoints: false, extraTime: false, autoClick: false });
    setIsGameActive(true);
  };

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô
  const addScore = (points) => {
    if (!isGameActive) return;

    let finalPoints = points * multiplier;
    if (powerUps.doublePoints) {
      finalPoints *= 2;
    }

    const newScore = score + finalPoints;
    const newStreak = clickStreak + 1;

    setScore(newScore);
    setClickStreak(newStreak);

    // ‡πÄ‡∏•‡πÄ‡∏ß‡∏•‡∏≠‡∏±‡∏û
    const newLevel = Math.floor(newScore / 100) + 1;
    if (newLevel > level) {
      setLevel(newLevel);
      setMultiplier(1 + (newLevel - 1) * 0.5);
    }

    // Streak bonus
    if (newStreak % 10 === 0) {
      setScore(prev => prev + 50); // Bonus!
    }

    // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó High Score
    if (newScore > highScore) {
      setHighScore(newScore);
    }
  };

  // ‡πÉ‡∏ä‡πâ Power-ups
  const activatePowerUp = (type) => {
    if (type === "doublePoints" && !powerUps.doublePoints) {
      setPowerUps(prev => ({ ...prev, doublePoints: true }));
      setTimeout(() => {
        setPowerUps(prev => ({ ...prev, doublePoints: false }));
      }, 5000); // 5 ‡∏ß‡∏¥
    }
    if (type === "extraTime" && !powerUps.extraTime) {
      setTimeLeft(prev => prev + 10);
      setPowerUps(prev => ({ ...prev, extraTime: true }));
    }
    if (type === "autoClick" && !powerUps.autoClick) {
      setPowerUps(prev => ({ ...prev, autoClick: true }));
      let auto = setInterval(() => addScore(1), 1000);
      setTimeout(() => {
        clearInterval(auto);
        setPowerUps(prev => ({ ...prev, autoClick: false }));
      }, 5000);
    }
  };

  // ‡∏à‡∏ö‡πÄ‡∏Å‡∏°
  const endGame = () => {
    setIsGameActive(false);
    if (score > highScore) {
      setHighScore(score);
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6">
      <h2 className="text-2xl font-bold mb-6 text-purple-600 text-center">
        üéÆ ‡πÄ‡∏Å‡∏°‡∏ô‡∏±‡∏ö‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô (Power-ups Edition)
      </h2>

      {/* Game Stats */}
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="bg-blue-50 p-4 rounded-lg text-center">
          <div className="text-2xl font-bold text-blue-600">{score}</div>
          <div className="text-sm text-gray-600">‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô</div>
        </div>
        <div className="bg-green-50 p-4 rounded-lg text-center">
          <div className="text-2xl font-bold text-green-600">{highScore}</div>
          <div className="text-sm text-gray-600">‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î</div>
        </div>
        <div className="bg-purple-50 p-4 rounded-lg text-center">
          <div className="text-2xl font-bold text-purple-600">Lv.{level}</div>
          <div className="text-sm text-gray-600">‡πÄ‡∏•‡πÄ‡∏ß‡∏•</div>
        </div>
        <div className="bg-orange-50 p-4 rounded-lg text-center">
          <div className="text-2xl font-bold text-orange-600">{timeLeft}s</div>
          <div className="text-sm text-gray-600">‡πÄ‡∏ß‡∏•‡∏≤‡πÄ‡∏´‡∏•‡∏∑‡∏≠</div>
        </div>
      </div>

      {/* Power-ups */}
      {isGameActive && (
        <div className="grid grid-cols-3 gap-2 mb-6">
          <button
            onClick={() => activatePowerUp("doublePoints")}
            disabled={powerUps.doublePoints}
            className={`px-3 py-2 rounded-lg text-white font-bold text-sm ${powerUps.doublePoints ? "bg-gray-400" : "bg-yellow-500 hover:bg-yellow-600"}`}
          >
            ‚ú® Double Points
          </button>
          <button
            onClick={() => activatePowerUp("extraTime")}
            disabled={powerUps.extraTime}
            className={`px-3 py-2 rounded-lg text-white font-bold text-sm ${powerUps.extraTime ? "bg-gray-400" : "bg-blue-500 hover:bg-blue-600"}`}
          >
            ‚è± +10s
          </button>
          <button
            onClick={() => activatePowerUp("autoClick")}
            disabled={powerUps.autoClick}
            className={`px-3 py-2 rounded-lg text-white font-bold text-sm ${powerUps.autoClick ? "bg-gray-400" : "bg-green-500 hover:bg-green-600"}`}
          >
            ü§ñ Auto Click
          </button>
        </div>
      )}

      {/* Game Controls */}
      {!isGameActive ? (
        <div className="text-center mb-6">
          <button
            onClick={startGame}
            className="bg-purple-500 hover:bg-purple-600 text-white px-8 py-4 rounded-lg font-bold text-xl"
          >
            üöÄ ‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏Å‡∏°
          </button>
          {score > 0 && (
            <div className="mt-4 p-4 bg-gray-100 rounded-lg">
              <p className="text-lg font-bold">‡πÄ‡∏Å‡∏°‡∏à‡∏ö‡πÅ‡∏•‡πâ‡∏ß!</p>
              <p>‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏£‡∏ß‡∏°: <span className="text-purple-600 font-bold">{score}</span></p>
              <p>‡πÄ‡∏•‡πÄ‡∏ß‡∏•‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î: <span className="text-green-600 font-bold">{level}</span></p>
            </div>
          )}
        </div>
      ) : (
        <div className="space-y-4 mb-6">
          <div className="grid grid-cols-2 gap-4">
            <button
              onClick={() => addScore(1)}
              className="bg-green-500 hover:bg-green-600 text-white py-4 rounded-lg font-bold text-lg"
            >
              +1 ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô
            </button>
            <button
              onClick={() => addScore(5)}
              className="bg-blue-500 hover:bg-blue-600 text-white py-4 rounded-lg font-bold text-lg"
            >
              +5 ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô
            </button>
          </div>
          <button
            onClick={() => addScore(10)}
            className="w-full bg-red-500 hover:bg-red-600 text-white py-4 rounded-lg font-bold text-lg"
          >
            +10 ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô (‡πÇ‡∏ö‡∏ô‡∏±‡∏™!)
          </button>
          <button
            onClick={endGame}
            className="w-full bg-gray-500 hover:bg-gray-600 text-white py-2 rounded-lg"
          >
            ‡∏´‡∏¢‡∏∏‡∏î‡πÄ‡∏Å‡∏°
          </button>
        </div>
      )}
    </div>
  );
}

export default CounterGame;
