import React, { useState, useEffect } from 'react';

// à¸Šà¸·à¹ˆà¸­ Key à¸ªà¸³à¸«à¸£à¸±à¸šà¹ƒà¸Šà¹‰à¹ƒà¸™ Local Storage
const LOCAL_STORAGE_KEY = 'movieWishlistApp.movies';

function App() {
  // State 1: à¹€à¸à¹‡à¸šà¸£à¸²à¸¢à¸Šà¸·à¹ˆà¸­à¸«à¸™à¸±à¸‡à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸” à¹€à¸›à¹‡à¸™ Array à¸‚à¸­à¸‡ Object
  const [movies, setMovies] = useState([]);

  // State 2: à¹€à¸à¹‡à¸šà¸„à¹ˆà¸²à¸ˆà¸²à¸à¸Šà¹ˆà¸­à¸‡ input à¸ªà¸³à¸«à¸£à¸±à¸šà¹€à¸žà¸´à¹ˆà¸¡à¸«à¸™à¸±à¸‡à¹ƒà¸«à¸¡à¹ˆ
  const [inputValue, setInputValue] = useState('');

  // useEffect 1: à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸ˆà¸²à¸ Local Storage à¸•à¸­à¸™à¹€à¸›à¸´à¸”à¹à¸­à¸›à¸„à¸£à¸±à¹‰à¸‡à¹à¸£à¸
  useEffect(() => {
    const storedMovies = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    if (storedMovies) {
      setMovies(storedMovies);
    }
  }, []); // Dependency array à¸§à¹ˆà¸²à¸‡à¹€à¸›à¸¥à¹ˆà¸² `[]` à¸«à¸¡à¸²à¸¢à¸–à¸¶à¸‡à¹ƒà¸«à¹‰à¸—à¸³à¸‡à¸²à¸™à¹à¸„à¹ˆà¸„à¸£à¸±à¹‰à¸‡à¹€à¸”à¸µà¸¢à¸§à¸•à¸­à¸™ mount

  // useEffect 2: à¸šà¸±à¸™à¸—à¸¶à¸à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸¥à¸‡ Local Storage à¸—à¸¸à¸à¸„à¸£à¸±à¹‰à¸‡à¸—à¸µà¹ˆ `movies` state à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™à¹à¸›à¸¥à¸‡
  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(movies));
  }, [movies]); // Dependency array à¸„à¸·à¸­ `[movies]` à¸«à¸¡à¸²à¸¢à¸–à¸¶à¸‡à¹ƒà¸«à¹‰à¸—à¸³à¸‡à¸²à¸™à¸—à¸¸à¸à¸„à¸£à¸±à¹‰à¸‡à¸—à¸µà¹ˆ movies à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™

  // Function: à¸ˆà¸±à¸”à¸à¸²à¸£à¹€à¸¡à¸·à¹ˆà¸­à¸à¸”à¸›à¸¸à¹ˆà¸¡ Add
  const handleAddMovie = (e) => {
    e.preventDefault(); // à¸›à¹‰à¸­à¸‡à¸à¸±à¸™à¹„à¸¡à¹ˆà¹ƒà¸«à¹‰à¸Ÿà¸­à¸£à¹Œà¸¡ submit à¹à¸¥à¹‰à¸§à¸«à¸™à¹‰à¸² refresh
    if (inputValue.trim() === '') return; // à¸–à¹‰à¸² input à¸§à¹ˆà¸²à¸‡à¹€à¸›à¸¥à¹ˆà¸² à¹„à¸¡à¹ˆà¸•à¹‰à¸­à¸‡à¸—à¸³à¸­à¸°à¹„à¸£

    const newMovie = {
      id: Date.now(), // à¹ƒà¸Šà¹‰ timestamp à¹€à¸›à¹‡à¸™ ID à¹à¸šà¸šà¸‡à¹ˆà¸²à¸¢à¹†
      text: inputValue,
      watched: false,
    };

    setMovies(prevMovies => [newMovie, ...prevMovies]); // à¹€à¸žà¸´à¹ˆà¸¡à¸«à¸™à¸±à¸‡à¹ƒà¸«à¸¡à¹ˆà¹€à¸‚à¹‰à¸²à¹„à¸›à¸šà¸™à¸ªà¸¸à¸”à¸‚à¸­à¸‡ Array
    setInputValue(''); // à¹€à¸„à¸¥à¸µà¸¢à¸£à¹Œà¸Šà¹ˆà¸­à¸‡ input
  };

  // Function: à¸ˆà¸±à¸”à¸à¸²à¸£à¹€à¸¡à¸·à¹ˆà¸­à¸à¸”à¸›à¸¸à¹ˆà¸¡à¸¥à¸š
  const handleDeleteMovie = (id) => {
    setMovies(prevMovies => prevMovies.filter(movie => movie.id !== id));
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>ðŸŽ¬ Movie Wishlist</h1>
        <p>What do you want to watch next?</p>
      </header>

      <main>
        <form className="add-movie-form" onSubmit={handleAddMovie}>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="e.g., The Matrix"
          />
          <button type="submit">Add</button>
        </form>

        <section className="movie-wishlist">
          <h2>My List</h2>
          {movies.length > 0 ? (
            <ul className="movie-list">
              {movies.map(movie => (
                <li key={movie.id} className="movie-item">
                  <span>{movie.text}</span>
                  <button onClick={() => handleDeleteMovie(movie.id)}>
                    &times; {/* This is the 'X' symbol */}
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p className="empty-list-message">Your wishlist is empty. Add a movie to get started!</p>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
